import React, { useState, useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text, Float } from '@react-three/drei';
import './App.css';
function AnimatedBook({ isProcessing }) {
  const bookRef = useRef();
  
  useFrame((state) => {
    if (bookRef.current) {
      bookRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.1;
      if (isProcessing) {
        bookRef.current.rotation.x = Math.sin(state.clock.elapsedTime * 2) * 0.05;
      }
    }
  });

  return (
    <group ref={bookRef}>
      {/* Book Cover */}
      <mesh rotation={[0, 0, 0]} position={[0, 0, 0.5]}>
        <boxGeometry args={[6, 8, 1]} />
        <meshStandardMaterial color="#3a86ff" />
      </mesh>
      
      {/* Book Pages */}
      <mesh rotation={[0, 0, 0]} position={[0, 0, 0]}>
        <boxGeometry args={[5.8, 7.8, 0.8]} />
        <meshStandardMaterial color="#f1faee" />
      </mesh>
      
      {/* Book Title */}
      <Text
        position={[0, 2, 1]}
        fontSize={0.5}
        color="#1d3557"
        anchorX="center"
        anchorY="middle"
      >
        EchoVerse
      </Text>
    </group>
  );
}
function App() {
  const [text, setText] = useState('');
  const [tone, setTone] = useState('neutral');
  const [voice, setVoice] = useState('en-US_AllisonV3Voice');
  const [isProcessing, setIsProcessing] = useState(false);
  const [rewrittenText, setRewrittenText] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [showComparison, setShowComparison] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    
    setIsProcessing(true);
    
    try {
      const response = await fetch('http://localhost:5000/api/generate-audiobook', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text, tone, voice }),
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setRewrittenText(data.rewritten_text);
        setAudioUrl(`http://localhost:5000${data.audio_url}`);
        setShowComparison(true);
      } else {
        console.error('Error:', data.error);
        alert('Failed to generate audiobook: ' + data.error);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to generate audiobook');
    } finally {
      setIsProcessing(false);
    }
  };

  const downloadAudio = () => {
    if (audioUrl) {
      const a = document.createElement('a');
      a.href = audioUrl;
      a.download = 'echoverse-audiobook.mp3';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <h1>EchoVerse</h1>
          <p>AI-Powered Audiobook Creation</p>
        </div>
        
        <div className="content">
          <div className="visualization">
            <Canvas camera={{ position: [0, 0, 12], fov: 50 }}>
              <ambientLight intensity={0.5} />
              <pointLight position={[10, 10, 10]} />
              <AnimatedBook isProcessing={isProcessing} />
              <OrbitControls enableZoom={false} />
            </Canvas>
          </div>
          
          <div className="controls">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="text">Enter your text:</label>
                <textarea
                  id="text"
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  placeholder="Paste your text here or upload a file..."
                  rows="8"
                  required
                ></textarea>
              </div>
              
              <div className="form-group">
                <label htmlFor="tone">Select tone:</label>
                <select
                  id="tone"
                  value={tone}
                  onChange={(e) => setTone(e.target.value)}
                >
                  <option value="neutral">Neutral</option>
                  <option value="suspenseful">Suspenseful</option>
                  <option value="inspiring">Inspiring</option>
                </select>
              </div>
              
              <div className="form-group">
                <label htmlFor="voice">Select voice:</label>
                <select
                  id="voice"
                  value={voice}
                  onChange={(e) => setVoice(e.target.value)}
                >
                  <option value="en-US_AllisonV3Voice">Allison (US English)</option>
                  <option value="en-US_LisaV3Voice">Lisa (US English)</option>
                  <option value="en-US_MichaelV3Voice">Michael (US English)</option>
                </select>
              </div>
              
              <button type="submit" disabled={isProcessing}>
                {isProcessing ? 'Generating...' : 'Generate Audiobook'}
              </button>
            </form>
            
            {audioUrl && (
              <div className="audio-controls">
                <audio controls src={audioUrl} className="audio-player"></audio>
                <button onClick={downloadAudio} className="download-btn">
                  Download MP3
                </button>
              </div>
            )}
          </div>
        </div>
        
        {showComparison && (
          <div className="comparison">
            <h2>Text Comparison</h2>
            <div className="comparison-content">
              <div className="original">
                <h3>Original Text</h3>
                <p>{text}</p>
              </div>
              <div className="rewritten">
                <h3>Tone-Adapted Text</h3>
                <p>{rewrittenText}</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
